import React, { useState, useEffect } from 'react'
import { ValidatorForm, TextValidator } from 'react-material-ui-form-validator'
import { Breadcrumb, SimpleCard } from 'app/components'
import ReactHtmlParser from 'react-html-parser'

import {
    Button,
    Icon,
    Grid,
    Radio,
    RadioGroup,
    FormControlLabel,
    Checkbox,
    Table,
    TableHead,
    TableCell,
    TableBody,
    TableRow,
} from '@material-ui/core'
import {
    MuiPickersUtilsProvider,
    KeyboardDatePicker,
} from '@material-ui/pickers'
import 'date-fns'
import DateFnsUtils from '@date-io/date-fns'

const Typography = () => {
    let list = [{
        className: '.whitespace-no-wrap',
        description: 'white-space: nowrap',
    }]
}



const OrdenServicio = () => {
    const [state, setState] = useState({
        date: new Date(),
    })


    useEffect(() => {
        ValidatorForm.addValidationRule('isPasswordMatch', (value) => {
            console.log(value)

            if (value !== state.password) {
                return false
            }
            return true
        })
        return () => ValidatorForm.removeValidationRule('isPasswordMatch')
    }, [state.password])

    const handleSubmit = (event) => {
        // console.log("submitted");
        // console.log(event);
    }

    const handleChange = (event) => {
        event.persist()
        setState({
            ...state,
            [event.target.name]: event.target.value,
        })
    }

    const handleDateChange = (date) => {
        setState({ ...state, date })
    }

    const {
        username,
        firstName,
        creditCard,
        mobile,
        password,
        confirmPassword,
        gender,
        date,
        email,
    } = state

    return (
        <div>
            <ValidatorForm onSubmit={handleSubmit} onError={() => null}>
                <Grid container spacing={6}>
                    <Grid item lg={6} md={6} sm={6} xs={6}>
                        <TextValidator
                            className="mb-4 w-full"
                            label="Nombre"
                            onChange={handleChange}
                            type="text"
                            name="firstName"
                            value={firstName || ''}
                            validators={['required']}
                            errorMessages={['Campo Requerido']}
                        />
                        <TextValidator
                            className="mb-4 w-full"
                            label="Apellido"
                            onChange={handleChange}
                            type="text"
                            name="username"
                            value={username || ''}
                            validators={[
                                'required',
                                'minStringLength: 4',
                                'maxStringLength: 9',
                            ]}
                            errorMessages={['Campo Requerido']}
                        />
                        <TextValidator
                            className="mb-4 w-full"
                            label="Email"
                            onChange={handleChange}
                            type="email"
                            name="email"
                            value={email || ''}
                            validators={['required', 'isEmail']}
                            errorMessages={[
                                'Campo Requerido',
                                'email is not valid',
                            ]}
                        />

                        <TextValidator
                            className="mb-8 w-full"
                            label="Celular"
                            onChange={handleChange}
                            type="number"
                            name="creditCard"
                            value={creditCard || ''}
                            validators={[
                                'required',
                                'minStringLength:16',
                                'maxStringLength: 16',
                            ]}
                            errorMessages={['Campo Requerido']}
                        />
                    </Grid>
                    <Grid item lg={6} md={6} sm={12} xs={12}>
                        <TextValidator
                            className="mb-4 w-full"
                            label="Direccion"
                            onChange={handleChange}
                            type="text"
                            name="mobile"
                            value={mobile || ''}
                            validators={['required']}
                            errorMessages={['Campo Requerido']}
                        />
                        <TextValidator
                            className="mb-4 w-full"
                            label="Otro"
                            onChange={handleChange}
                            name="password"
                            type="firstName"
                            value={password || ''}
                            errorMessages={['Campo Requerido']}
                        />
                        <TextValidator
                            className="mb-4 w-full"
                            label="Otro"
                            onChange={handleChange}
                            name="confirmPassword"
                            type="firstName"
                            value={confirmPassword || ''}
                            validators={['isPasswordMatch']}
                            errorMessages={[
                                'Campo Requerido',
                                'password no concuerda',
                            ]}
                        />

                        <FormControlLabel
                            control={<Checkbox />}
                            label="Deseo Recibir informacion mediante correo"
                        />
                    </Grid>
                </Grid>

                <Grid item lg={6} md={6} sm={12} xs={12}>
        <div className="m-sm-30">
            <div className="mb-sm-30">
                <Breadcrumb
                    routeSegments={[
                        { name: 'Utilities', path: '/utilities' },
                        { name: 'Typography' },
                    ]}
                />
            </div>

            <SimpleCard title="typography">
                <Table>
                    <TableHead>
                        <TableRow className="bg-default">
                            <TableCell className="px-0">Class Name</TableCell>
                            <TableCell className="px-0">Size</TableCell>
                            <TableCell className="px-0">Example</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {[...Array(7).keys()]
                            .slice(1)
                            .reverse()
                            .map((item, index) => (
                                <TableRow key={index}>
                                    <TableCell className="px-0">
                                        h{item}
                                    </TableCell>
                                    <TableCell className="px-0 capitalize"></TableCell>
                                    <TableCell className="px-0 capitalize">
                                        {ReactHtmlParser(
                                            `<h${item}>h${item}</h${item}>`
                                        )}
                                    </TableCell>
                                </TableRow>
                            ))}
                        <TableRow>
                            <TableCell className="px-0">text-small</TableCell>
                            <TableCell className="px-0 capitalize">
                                0.8125rem
                            </TableCell>
                            <TableCell className="px-0 capitalize">
                                <span className="text-small">text-small</span>
                            </TableCell>
                        </TableRow>

                        {[13, 14, 16, 18, 20, 22, 24, 30, 32].map(
                            (item, index) => (
                                <TableRow key={index}>
                                    <TableCell className="px-0">
                                        text-{item}
                                    </TableCell>
                                    <TableCell className="px-0 capitalize">
                                        {item}px
                                    </TableCell>
                                    <TableCell className="px-0 capitalize">
                                        {ReactHtmlParser(
                                            `<span class="text-${item}">text-${item}</span>`
                                        )}
                                    </TableCell>
                                </TableRow>
                            )
                        )}
                    </TableBody>
                </Table>

                <div className="py-2"></div>
                {list.map((item, ind) => (
                    <div
                        key={ind}
                        className="flex items-center flex-wrap justify-between"
                    >
                        <code>{item.className}</code>
                        <div className="highlight-js">
                            <Highlight className="html">{`<div className="${item.className.replace(
                                '.',
                                ''
                            )}">${item.description}</div>`}</Highlight>
                        </div>
                    </div>
                ))}
            </SimpleCard>
        </div>
                    </Grid>

                <Button color="primary" variant="contained" type="submit">
                    <Icon>send</Icon>
                    <span className="pl-2 capitalize">Enviar</span>
                </Button>
            </ValidatorForm>
        </div>
    )
}

export default OrdenServicio
